(204, 1, '2025-07-16 10:20:31', '2025-07-16 10:20:31', '<!-- wp:image {\"id\":203,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/smpm2pagelaran/wp-content/uploads/2025/07/smpm2p.jpg\" alt=\"\" class=\"wp-image-203\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>Visi</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Terwujudnya generasi yang berakhlak mulia, cerdas, terampil, dan berwawasan lingkungan</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>Misi</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">pembentukan karakter siswa melalui pendidikan agama dan budi pekerti, peningkatan kualitas pembelajaran yang aktif, inovatif, kreatif, efektif, dan menyenangkan, pengembangan potensi siswa di bidang akademik dan non-akademik, serta menciptakan lingkungan sekolah yang Islami, disiplin, dan hijau.&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\"><strong>Tujuan Sekolah</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">SMP Muhammadiyah 2 Pagelaran berharap dapat melahirkan generasi penerus bangsa yang berakhlak mulia, cerdas, terampil, dan memiliki kesadaran akan pentingnya menjaga lingkungan.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'Visi &amp; Misi SMP Muhammadiyah 2 Pagelaran', '', 'inherit', 'closed', 'closed', '', '180-revision-v1', '', '', '2025-07-16 10:20:31', '2025-07-16 10:20:31', '', 180, 'http://localhost/smpm2pagelaran/?p=204', 0, 'revision', '', 0),
